/**
 * Javascript Objects
 * These are not Json which are strings (javascript (literal) notation),
 * these are plain old javascript objects
 */
export declare type PojoPrimitive = string | boolean | number | null | undefined;
export declare const isPojoPrimitve: (value: unknown) => value is PojoPrimitive;
export declare type PojoObject = {
    [key: string]: Pojo;
};
export declare const isPojoObject: (value: unknown) => value is PojoObject;
export declare type PojoArray = Pojo[];
export declare const isPojoArray: (value: unknown) => value is PojoArray;
export declare type Pojo = PojoPrimitive | PojoObject | PojoArray;
export declare const isPojo: (value: unknown) => value is Pojo;
export declare function assert_is_pojo(value: unknown): asserts value is Pojo;
